#!/usr/bin/env node
"use strict";

function main() {
  if (process.env.SUPPRESS_SUPPORT || process.env.OPENCOLLECTIVE_HIDE || process.env.CI) {
    return;
  }

  try {
    var Configstore = require('configstore');

    var pkg = require(__dirname + '/../package.json');

    var now = Date.now();
    var week = 1000 * 60 * 60 * 24 * 7; // create a Configstore instance with an unique ID e.g.
    // Package name and optionally some default values

    var conf = new Configstore(pkg.name);
    var last = conf.get('lastCheck');

    if (!last || now - week > last) {
      console.log("\x1B[32mLove nodemon? You can now support the project via the open collective:\x1B[22m\x1B[39m\n > \x1B[96m\x1B[1mhttps://opencollective.com/nodemon/donate\x1B[0m\n");
      conf.set('lastCheck', now);
    }
  } catch (e) {
    console.log("\x1B[32mLove nodemon? You can now support the project via the open collective:\x1B[22m\x1B[39m\n > \x1B[96m\x1B[1mhttps://opencollective.com/nodemon/donate\x1B[0m\n");
  }
}

main();